			progress.start();
			requestService.updateCertificateIsExposeState(certificate.databaseID, certificate.isExpose).then(function() {
				progress.success();
			}, function(error) {
				certificate.isExpose = oldIsExpose;
				progress.reset();
				Popup.showErrorPopup(error.message); 
			});
