(function() {

"use strict";

angular.module("BitriseWorkflowEditor").controller("DefaultPopupController", function($scope, $rootScope, Popup) {

	var viewModel = this;

	var types = [{
		id: "success",
		cssClass: "success"
	}, {
		id: "error",
		cssClass: "error"
	}, {
		id: "notice",
		cssClass: "notice"
	}];

	viewModel.alertPopup = new Popup({
		type: undefined,
		title: undefined,
		details: undefined,
		okTitle: undefined,
		deferer: undefined
	});

	viewModel.confirmPopup = new Popup({
		title: undefined,
		details: undefined,
		yesTitle: undefined,
		noTitle: undefined,
		yesSelected: undefined,
		noSelected: undefined,
		deferer: undefined
	});

	$rootScope.$on("DefaultPopupController::showAlertPopup", function(event, typeID, title, details, okTitle, deferer) {
		viewModel.alertPopup.parameters.type = _.find(types, {
			id: typeID ? typeID : "error"
		});
		viewModel.alertPopup.parameters.title = title;
		viewModel.alertPopup.parameters.details = typeof details == "string" ? [details] : details;
		viewModel.alertPopup.parameters.okTitle = okTitle ? okTitle : "OK";

		if (deferer && deferer.resolve) {
			viewModel.alertPopup.beforeDismissCallback = deferer.resolve;
		}

		viewModel.alertPopup.isVisible = true;
	});

	$rootScope.$on("DefaultPopupController::showConfirmPopup", function(event, title, details, yesTitle, noTitle, yesSelected, noSelected, deferer) {
		viewModel.confirmPopup.parameters.title = title;
		viewModel.confirmPopup.parameters.details = typeof details == "string" ? [details] : details;
		viewModel.confirmPopup.parameters.yesTitle = yesTitle ? yesTitle : "Yes";
		viewModel.confirmPopup.parameters.noTitle = noTitle ? noTitle : "No";

		viewModel.confirmPopup.parameters.yesSelected = function() {
			if (yesSelected) {
				yesSelected();
			}

			if (deferer && deferer.resolve) {
				deferer.resolve();
			}

			viewModel.confirmPopup.beforeDismissCallback = undefined;
			viewModel.confirmPopup.isVisible = false;
		};

		viewModel.confirmPopup.parameters.noSelected = function() {
			if (noSelected) {
				noSelected();
			}

			if (deferer && deferer.reject) {
				deferer.reject();
			}

			viewModel.confirmPopup.beforeDismissCallback = undefined;
			viewModel.confirmPopup.isVisible = false;
		};

		viewModel.confirmPopup.beforeDismissCallback = function() {
			noSelected();

			if (deferer && deferer.reject) {
				deferer.reject();
			}
		};

		viewModel.confirmPopup.isVisible = true;
	});

});

})();
(function() {

"use strict";

angular.module("BitriseWorkflowEditor").controller("EnvVarsController", function($scope, $rootScope, $q, requestService, stringService, appService, Progress, Variable) {

	var viewModel = this;

	$scope.Variable = Variable;

	var mode;
	var envVarsWatchers;

	viewModel.envVarsBySource;

	$scope.$on("$destroy", $rootScope.$on("MainController::changesDiscarded", function() {
		switch (mode) {
			case "secrets":
				configureWithSecrets();

				break;
			case "env-vars":
				configureWithAppConfig();

				break;
		}
	}));

	viewModel.configureWithMode = function(_mode) {
		mode = _mode;

		switch (mode) {
			case "secrets":
				configureWithSecrets();

				break;
			case "env-vars":
				configureWithAppConfig();

				break;
		}
	};

	function configureWithSecrets() {
		viewModel.envVarsBySource = [{
			type: "secrets",
			notification: "Secret Environment Variables <strong>will not be available</strong> in builds triggered by pull requests. You can add private information here.",
			envVars: appService.secrets
		}];

		configureEnvVarsWatchers();
	}

	function configureWithAppConfig() {
		viewModel.envVarsBySource = [];

		viewModel.envVarsBySource.push({
			type: "app",
			notification: "App Environment Variables will also be available in builds triggered by pull requests. You should NOT add any private information here.",
			envVars: _.map(appService.appConfig.app && appService.appConfig.app.envs ? appService.appConfig.app.envs : [], function(anAppEnvVarConfig) {
				return new Variable(anAppEnvVarConfig, Variable.defaultVariableConfig());
			})
		});

		_.each(appService.appConfig.workflows, function(aWorkflowConfig, aWorkflowID) {
			viewModel.envVarsBySource.push({
				type: "workflow",
				workflowID: aWorkflowID,
				notification: stringService.stringReplacedWithParameters("You can specify Env Vars which will only be available for the steps in your <workflow_id> Workflow.", {
					workflow_id: aWorkflowID
				}),
				envVars: _.map(aWorkflowConfig.envs, function(anEnvVarConfig) {
					return new Variable(anEnvVarConfig, Variable.defaultVariableConfig());
				})
			});
		});

		configureEnvVarsWatchers();
	}

	function configureEnvVarsWatchers() {
		if (envVarsWatchers) {
			_.each(envVarsWatchers, function(anEnvVarsWatcher) {
				anEnvVarsWatcher();
			});
		}

		envVarsWatchers = _.map(viewModel.envVarsBySource, function(envVarsOfSource) {
			return $scope.$watch(function() {
				return envVarsOfSource;
			}, function(envVarsOfSource) {
				switch (envVarsOfSource.type) {
					case "app":
						if (!appService.appConfig) {
							return;
						}

						if (!appService.appConfig.app) {
							appService.appConfig.app = {};
						}

						appService.appConfig.app.envs = _.map(envVarsOfSource.envVars, function(anEnvVar) {
							return anEnvVar.userVariableConfig;
						});

						if (_.isEmpty(appService.appConfig.app.envs)) {
							delete appService.appConfig.app["envs"];
						}

						if (_.isEmpty(appService.appConfig.app)) {
							delete appService.appConfig["app"];
						}

						break;
					case "workflow": {
						if (!appService.appConfig) {
							return;
						}

						appService.appConfig.workflows[envVarsOfSource.workflowID].envs = _.map(envVarsOfSource.envVars, function(anEnvVar) {
							return anEnvVar.userVariableConfig;
						});

						if (_.isEmpty(appService.appConfig.workflows[envVarsOfSource.workflowID].envs)) {
							delete appService.appConfig.workflows[envVarsOfSource.workflowID]["envs"];
						}

						break;
					}
				}
			}, true);
		});
	}

	viewModel.addToEnvVars = function(envVars) {
		var newEnvVar = new Variable({
			"": ""
		}, Variable.defaultVariableConfig());

		envVars.push(newEnvVar);
	};

	viewModel.removeEnvVarFromEnvVars = function(envVar, envVars) {
		var index = _.indexOf(envVars, envVar);

		envVars.splice(index, 1);
	};

});

})();
